import React, { useState } from "react";
import TaskCard from "./TaskCard";
import { AddTaskCardButton } from "./button/AddTaskCardButton";
import { DragDropContext, Droppable } from "react-beautiful-dnd";

const reorder =(taskCardsList,startIndex, endIndes)=>{
  const remove = taskCardsList.splice(startIndex,1);
  taskCardsList.splice(endIndex,0,remove[0]);
}

export const TaskCards = () => {
  const [taskCardsList, setTaskCardsList] = useState([{
    id:"0",
    draggableId:"item0"
  }
  ]);

  const handleDragEnd=()=>{
    reorder(taskCardsList,result.source.index,result.destination.index)

  }
  return (
    <DragDropContext onDragEnd={handleDragEnd}>
      <Droppable droppableId="droppable" direction="horizontal">
        {(provided)=>(
      <div className="taskCardsArea" {...provided.droppableProps} ref={provided.innerRef}>
      {taskCardsList.map((taskCard,index) => (
        <TaskCard key={taskCard.id}
        index={index}
        taskCardsList={taskCardsList}
        setTaskCardsList={setTaskCardsList}
        taskCard={taskCard}
        />
      ))}
      {provided.placeholder}
      <AddTaskCardButton
        taskCardsList={taskCardsList}
        setTaskCardsList={setTaskCardsList}
      />
    </div>
        )}

      </Droppable>
    </DragDropContext>
  );
};
