import React from "react";
import Task from "./Task";
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";

export default function Tasks({ taskList, setTaskList = [] }) {
  // if (!taskList) return null;
  return (
    <Draggable index={task.id} draggableId={task.draggableId}>
      {(provided) => (
        <div className="taskBox" key={task.id} ref={provided.innerRef}
        {...provided.draggableProps}
        >
          <DragDropContext>
            <Droppable droppableId="droppable">
              {(provided) => (
                <div {...provided.proppableProps} ref={provided.innerRef}>
                  {taskList.map((task, index) => (
                    <div key={index}>
                      <Task
                        task={task}
                        taskList={taskList}
                        setTaskList={setTaskList}
                      />
                    </div>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </DragDropContext>
        </div>
      )}
    </Draggable>
  );
}
