import React from "react";
import { DragDropContext, Droppable } from "react-beautiful-dnd";
import { Task } from "./Task";
import TaskCard from "./TaskCard";

const reorder = (taskList, startIndex, endIndex) => {
  const result = Array.from(taskList);
  const [removed] = result.splice(startIndex, 1);
  result.splice(endIndex, 0, removed);
  return result;
};


export const Tasks = ({ taskList, setTaskList }) => {
  const handleDrangEnd = (result) => {
    if (!result.destination) return; 

    const reorderedList = reorder(taskList, result.source.index, result.destination.index);
    setTaskList(reorderedList);

  };

  return (
    <div>
      <DragDropContext onDragEnd={handleDrangEnd}>
        <Droppable droppableId="droppable">
          {(provided) => (
            <div {...provided.droppableProps} ref={provided.innerRef}>
              {taskList.map((task, index) => (

                  <TaskCard
                  key={task.id}
                    index={index}
                    task={task}
                    taskList={taskList}
                    setTaskList={setTaskList}
                  />

              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
};
