import React from "react";
import Task from "./Task";
import { DragDropContext } from "react-beautiful-dnd";

export default function Tasks({ taskList, setTaskList = [] }) {
  // if (!taskList) return null;
  return (
    <div>
      <DragDropContext>
        <Droppable doroppableId="droppable">

{(provided)=>(
  <div {...provided.proppableProps} ref={provided.innerRef}>
      {taskList.map((task, index) => (
        <div key={index}>
        <Task task={task} taskList={taskList} setTaskList={setTaskList}/>
        </div>
      ))}
  </div>
)}
        </Droppable>


      </DragDropContext>
    </div>
  );
}
